import {
	EXCEPTION_HTTP_REQUEST
} from 'dictionary'

import {Observable} from 'rxjs/Rx'
import {of} from 'rxjs/observable/of'

export function request$(url) {
	return Observable.ajax(url)
        .map(result => result.response)
        .catch((err) => {
            return of(EXCEPTION_HTTP_REQUEST);
        });
}