import React from 'react'
import PropTypes from 'prop-types'
import classnames from 'classnames'

export default ({option, selected, onClick})=> (
	<div className={classnames('tsla-configurator--option', {'selected': selected}, {'future-option': option.isFutureOption})} data-option={option.code} onClick={()=>onClick(option.code)}>
	    <span className='--option-group-name'>{option.name}</span>
	</div>
)



