import React from 'react'
import Option from './Option'

export default ({group, currentOptions, selectOption})=> (
	<div className={`tsla-configurator--group`} data-group={group.code}>
	    <h4 className='group--group-name'>{group.name}</h4>
        <div className='group--group-content'>
        {
            group.options.map((opt, key)=>{
                return <Option
                    key={key} 
                    option={opt} 
                    onClick={selectOption}
                    selected={currentOptions.includes(opt.code)}  
                />
            })
        }
        </div>   
	</div>
)