import {
	PRICE_CHANGE,
	FINANCIAL_PRODUCT_CHANGE
} from 'dictionary';

export const calculatorChangeEpic = (action$, store) =>
    action$.filter(action => [
            PRICE_CHANGE,
            FINANCIAL_PRODUCT_CHANGE
    	].includes(action.type)).map((action)=>{
        return {
        	type: 'UPDATE_PRICES'
        }
    })