window.onload = function() {
  if (window.tslaHeader) {
    window.tslaHeader()
  }
  var tslaDocumentationLink = document.querySelector('.tsla-header-nav--list_item_last');
  var tslaMenuCheckbox = document.getElementById('tsla-header-main--trigger');
  // if user clicks on the 'mask', uncheck all the checkboxes
  tslaDocumentationLink.addEventListener('click', function() {
    tslaMenuCheckbox.click();
  });
}

