/**
 * Returns configuration for moment.js
 * @param  {String} locale [description]
 * @return {Object} 
 */
module.exports.getLocaleSettings = function(locale) {

    // check if there is a config that exactly matches the locale e.g., 'en_US'
    var cfg = momentConfig[locale];

    if (!cfg) {

        // if particular language + market locale does not exist, default to locale that matches language
        var loc = Utils.parseLocale(locale);

        if (loc) {

            // check for config that matches language code e.g.: 'en', 'de', etc...
            cfg = momentConfig[loc.lang];

            // look for config with same language code as locale key
            if (!cfg) {
                var keys = Object.keys(momentConfig);
                for (var i = 0; i < keys.length; i++) {
                    var key = keys[i];
                    if (key.indexOf(loc.lang) === 0) {
                        cfg = momentConfig[key];
                        break;
                    }
                }
            }

            // Still no matching config? Default to 'en'
            if (!cfg) {
                cfg = momentConfig.en;
                console.error('Date format for ', locale, ' not found. Defaulting to en');
            }
        }
    }

    return cfg;
};

module.exports.configs = momentConfig;