var swaggerUtils = require('@web/tesla-utils/api/swaggerUtils');

function getRoutes(envConfig){

	var myApiRoutes = require('./api/locationRest')(envConfig);
	return swaggerUtils.getModuleRoutes(myApiRoutes);
}

module.exports.locationApi = require('./api/location');
module.exports.getRoutes = getRoutes;