/**
 * Sort Utils
 * @type {{dir: {ASC: number, DESC: number}}}
 */
module.exports = {

    // sort order constants
    ASC: 0,
    DESC: 1,

    /**
     * Sorts an array by date
     * @param arr {Array}
     * @param dateField {String} [optional]
     * @param dir {Enum{Sort.ASC, Sort.DESC}}
     * @returns {*}
     */
    byDate : function(arr, dateField, dir){

        if(!arr){
            console.error('Sort.byDate requires an array');
            return;
        }

        if(arguments.length < 3){
            dir = this.DESC;
        }

        arr.sort(function(a,b){

            var d1 = dateField ? a[dateField] : a,
                d2 = dateField ? b[dateField] : b;

            d1 = new Date(d1).getTime();
            d2 = new Date(d2).getTime();

            if(dir === this.ASC){
                return d1 - d2;
            }
            else{
                return d2 - d1;
            }

        });

        return arr;
    }
};