 /**
 * Return semver version string parts
 * @param  {String} versionString [Valid semver version string (1.2.3, etc...)]
 * @return {Object} with major, minor, patch 
 */
module.exports = function(versionString){

    const parts = versionString.split('.');

    if(parts.length < 3){
        throw versionString + ' not a valid semver string ' + getSemver.caller;
    }

    return {
        version: versionString,
        major: parts[0],
        minor: parts[1],
        patch: parts[2]
    }
}